import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import api from "../../Services/api"


function Detalhes() {
    const { id } = useParams()
    const [filme, setfilme] = useState({})

    useEffect(() => {
        async function loaddetails() {
            const response = await api.get(`/movie/${id}`, {
                params: {
                    api_key: "1c3a0bc2c4667ade8dc5844ddf1f3561",
                    language: "pt-BR"
                }
            })

            setfilme(response.data.results)

        }
        loaddetails()
    }, [id])

    console.log(filme)

    return (
        <>
            <div>
                <h1>Detalhes</h1>
            </div>

            <div>

                <h1>{filme.id}</h1>
                <p><b>{filme.name}</b></p> <br />
                <div><img src={`https://image.tmdb.org/t/p/w300/${filme.poster_path}`} alt={filme.original_name} /></div> <br />

            </div>
        </>
    )
}

export default Detalhes
