import { useEffect, useState } from "react";
import { Link } from "react-router-dom";


function Favoritos() {
    const [Filme, setFilme] = useState({})



    useEffect(() => {
        async function loadfavoritos() {
            const minhaLista = localStorage.getItem("@FilmesFavoritos")
            setFilme(JSON.parse(minhaLista))
        }
        loadfavoritos()
    }, [Filme])

    function excluirfilmes(id){
        var filtroFilmes = Filme.filter( (item) => {
            return (item.id !== id)
        })
        setFilme(filtroFilmes);
        localStorage.setItem('@FilmesFavoritos', JSON.stringify(filtroFilmes))
    }

    return (
        <div>
            <p key={Filme.map}>
                <button onClick={() => excluirfilmes(item.id)}>Excluir</button>
            </p>
        </div>
    )
}

export default Favoritos
