import { createContext, useState } from "react"
import { toast } from "react-toastify"
import apiLocal from "../API/apiLocal/api"

export const AuthContext = createContext()

export default function AuthProvider({ children }) {
    const [ user, setUser ] = useState('')
    const isAuth = !!user

    const iToken = localStorage.getItem("@vistaseToken")
    const token = JSON.parse(iToken)

    async function loginVerify() {
        try {
            const response = await apiLocal.get('/ListarUsuarioToken', {
                headers: {
                    Authorization: 'Bearer ' + `${token}`
                }
            })
            setUser(response.data.id)
        } catch (err) {
            toast.error('Erro de login')
            if(err.response.status === 401) {setUser('')}
        }
    }

    async function signIn({ email, password }) {
        try {
            const response = await apiLocal.post("/LoginUsuario", {
                email,
                password
            })
            return response
        } catch (err) {
            //toast.error(Erro ao fazer login)
        }
    }

    return (
        <AuthContext.Provider value={{ isAuth, loginVerify, signIn }}>
            {children}
        </AuthContext.Provider>
    )
}

