import { useState } from "react"
import React from "react"
import api from "../../services/api.js"

import "../../components/css/PaginaHome.css"

//a fazer:

//Melhorar CSS
//Melhorar responsibilidade 



function Inicio() {

    const [Pokemon, setPokemon] = useState("")
    const [Detalhes, setDetalhes] = useState(null)

    const handleClick = async () => {
        try {
            const response = await api.Detalhe(Pokemon)
            setDetalhes(response);
        } catch (error) {
            setDetalhes({ error: "Não encontrado" });
        }
    }


    return (
        <>
            <div>
                <h2>Pesquise por nome ou ID</h2> <br />
                <input value={Pokemon} onChange={event => setPokemon(event.target.value)} className="pesquisa" type="text" placeholder="Exemplo: pikachu ou 1" /> <br />
                <button onClick={handleClick}>Procurar</button>

                {Detalhes && (
                    Detalhes.error ? (
                        <h1>{Detalhes.error}</h1>
                    ) : (
                        <div>
                            <h1>{Detalhes.name}</h1>
                            <img src={Detalhes.sprites.front_default} alt="Imagine que tem algo legal aqui" />
                        </div>
                    )
                )}
            </div>
        </>
    )
}



export default Inicio
