import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import api from "../../services/api.js"

import "../../components/css/PaginaHome.css"

//a fazer:

//Mudar interger para string e usar state nas imagens
//Melhorar responsibilidade no css
//


function Inicio() {

    const [Pokemon, setPokemon] = useState([""])
    const [Sprites, setSprites] = useState([""])

    useEffect(() => {
        async function loadpokemons() {
            const response = await api.get("/pokemon/", {
                params: {
                    language: "pt-BR"
                }
            })
            setPokemon(response.data.results)
        }
        loadpokemons()

    }, [Pokemon])

    useEffect(() => {
        async function loadsprites() {
            const response2 = await api.get(`/pokemon/1`, {
                params: {
                    language: "pt-BR"
                }
            })
            setSprites(response2.data)
        }
        loadsprites()
    }, [Sprites])

    console.log(Pokemon)

    return (
        <>
            <div>
            <input type="text" placeholder="Pesquise por ID ou Nome (Exemplo: 1 ou pikachu" />
            </div>
        </>
    )
}


export default Inicio
