import { useState, useEffect } from "react"
import api from "../../services/api.js"

import "../../components/css/Pagina.css"

function Inicio() {

    const [Pokemon, setPokemon] = useState([""])
    const [Sprites, setSprites] = useState([""])

    useEffect(() => {
        async function loadpokemons() {
            const response = await api.get("/pokemon/", {
                params: {
                    language: "pt-BR"
                }
            })
            setPokemon(response.data.results)
        }
        loadpokemons()

    }, [Pokemon])

    useEffect(() => {
        async function loadsprites() {
            const response2 = await api.get(`/pokemon/${Poke.name}`, {
                params: {
                    language: "pt-BR"
                }
            })
            setSprites(response2.data)
        }
        loadsprites()
    }, [Sprites])

    console.log(Pokemon)

    return (
        <div>
            {Pokemon.map((Poke) => {
                return (
                    <a key={Poke.id}>
                        <b>{Poke.name}</b> <br/>
                        <img src={`https://pokeapi.co/api/v2/${Poke.name}/sprites/front_default`} alt={`${Poke.name}`}/> <br />
                    </a>
                )
            })}
        </div>
    )
}


export default Inicio
