            <div>
                <h2> Horario: {this.state.horas} </h2>
                <h2> Contador Positivo </h2>
                <Botao name="aumentar" acaoBtn={this.aumentar} />
                <h1> {this.state.contador} </h1>
                <Botao name="diminuir" acaoBtn={this.diminuir} />
                <Botao name="dobrar" />
            </div>
        )
    }
}

export default Contador
