import React, { Component } from "react"
import Botao from "./botao"
class Contador extends Component {

    constructor(props) {
        super(props)
        this.state = {
            contador: 0,
            frase: "Darkness. . . I can't see anything.",
            OnHand: "Nothing",
            FazerTochar: 0,
        }

        this.diminuir = this.diminuir.bind(this)
        this.aumentar = this.aumentar.bind(this)
    }

    diminuir() {
        if (this.state.contador === 0) {
            var state = this.state
            state.contador -= 0
            this.setState(state)
        } else {
            var state = this.state
            state.contador -= 1
            this.setState(state)
        }
    }

    aumentar() {
        if (this.state.contador === 5) {
            var state = this.state
            state.contador += 0
            this.setState(state)
        } else {
            var state = this.state
            state.contador += 1
            this.setState(state)

            if (this.state.contador === 5) {
                var state = this.state
                state.frase = "I can't carry anymore..."
            }
        }
    }

    tocha() {
        
        if (this.state.contador ===5) {
            var state = this.state
             
        }
        
        
        if (this.state.FazerTochar = 1) {
            var state = this.state
            state.OnHand = "Torch"
        }
    }

    render() {
        return (
            <div>
                <p>{this.state.frase}</p>
                <p>Equip: {this.state.OnHand}</p>
                <Botao name="Gather sticks" acaoBtn={this.aumentar} />
                <h1> {this.state.contador} </h1>
            </div>
        )
    }
}

export default Contador
